import React from "react";
import { Howl } from "howler";

function SoundBox(props) {
    const CallMySnd = (src) => {
          const MySnd = new Howl({
            src,
            html5: true
          });
          MySnd.play();
    }
    document.addEventListener('keydown', function(event){
        console.log(props)
	    let thekey = event.key;
        let themainkey = this.props.sndkey;
        if(thekey == themainkey){
            console.log("keyisit");
            CallMySnd(props.source)
        }
    });
  
    return (
   <div onClick={() => CallMySnd(props.source)} class="border-black border-solid border-2 w-28 h-28 my-3 mx-3 px-0 py-0 bg-slate-700 rounded-md grid items-center grid-cols-1 grid-rows-2 grid-flow-col scale-100 hover:scale-105 transition-all rotate-0 hovor:animate-ping">
    <div class="text-center text-4xl font-semibold text-slate-400">{props.sndkey}</div>
    <div class="text-center text-base text-slate-400">{props.name}</div>
   </div>
  );
}

export default SoundBox;
